mapper.select:
SELECT COUNT(0) FROM category
WHERE delFlag != '1'

    AND id != #{id}

AND name = #{name}

mapper.update:
UPDATE category SET
name = #{name},
sort = #{sort},
remark = #{remark}
WHERE id = #{id}

mapper.select:
SELECT COUNT(a.id) FROM first_title a
WHERE a.delFlag != '1'
AND a.categoryId = #{id}
