mapper.select:
SELECT id,name,pid FROM category WHERE delFlag != '1'
AND id = #{categoryId}
ORDER BY sort

SELECT a.id,a.name,a.sort,a.remark,concat(ifnull(c.name,''),'>',ifnull(b.name,'')) pname,a.pid,b.name parentName FROM category a LEFT JOIN category b ON a.pid = b.id LEFT JOIN category c ON b.pid = c.id WHERE a.delFlag != '1'
AND a.pid = #{categoryId} OR b.pid = #{categoryId}
ORDER BY a.sort LIMIT #{page},#{limit}

SELECT COUNT(0) FROM category
WHERE delFlag != '1'

    AND id != #{id}

AND name = #{name}

SELECT COUNT(a.id) FROM category a
WHERE a.delFlag != '1'
AND a.pid = #{id}

SELECT COUNT(a.id) FROM video a
WHERE a.delFlag != '1'
AND a.categoryId = #{id}
