mapper.select:
SELECT COUNT(0) FROM feedback
WHERE delFlag != '1'

    AND DATE_FORMAT(addTime, '%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(addTime, '%Y-%m-%d') <= #{endTime}

SELECT a.id,b.username,a.view,DATE_FORMAT(a.addTime, '%Y-%m-%d %H:%i:%s') addTime
FROM feedback a
LEFT JOIN sys_user b ON a.userId = b.id
WHERE a.delFlag != '1'

    AND DATE_FORMAT(a.addTime, '%Y-%m-%d') >= #{startTime}


    AND DATE_FORMAT(a.addTime, '%Y-%m-%d') <= #{endTime}

ORDER BY a.addTime DESC
LIMIT #{page},#{limit}
