mapper.select:
SELECT a.id,a.name,a.sort,a.remark,a.categoryId,b.name categoryName FROM first_title a
LEFT JOIN category b ON a.categoryId = b.id
WHERE a.delFlag != '1'

    AND a.categoryId = #{categoryId}

ORDER BY a.sort
LIMIT #{page},#{limit}

SELECT COUNT(0) FROM first_title
WHERE delFlag != '1'

    AND id != #{id}

AND name = #{name}

mapper.update:
UPDATE first_title SET
name = #{name},
sort = #{sort},
remark = #{remark},
categoryId = #{categoryId}
WHERE id = #{id}

mapper.select:
SELECT COUNT(a.id) FROM second_title a
WHERE a.delFlag != '1'
AND a.firstTitleId = #{id}
