mapper.update:
UPDATE sysuser SET
    password = #{password},
    updTime = #{updTime},
    updUserId = #{updUserId}
WHERE id = #{id}

mapper.select:
SELECT id,username,loginname,remark,addTime,login_time loginTime FROM sysuser
WHERE delFlag != '1'

    AND loginname = #{loginname}


    AND id != #{id}
